SELECT a.*, e.firstname, e.lastname, em.dateremain, em.dayoff FROM tblattendance as a INNER JOIN tblapplicant as e ON a.applicantID = e.applicantID INNER JOIN tblemployee as em ON a.employeeID = em.employeeID INNER JOIN tblclient as c ON a.clientID = c.clientID where c.clientID = 2 AND a.attendanceID IN (SELECT MAX(attendanceID) from tblattendance where dateschedule = '2017-02-02' GROUP BY employeeID )